(()=>{var r=document.getElementById("my-form");async function m(t){t.preventDefault();var e=document.getElementById("my-form-status"),a=new FormData(t.target);fetch(t.target.action,{method:r.method,body:a,headers:{Accept:"application/json"}}).then(n=>{n.ok?(e.innerHTML="Thanks for your submission!",r.reset()):n.json().then(o=>{Object.hasOwn(o,"errors")?e.innerHTML=o.errors.map(s=>s.message).join(", "):e.innerHTML="Oops! There was a problem submitting your form"})}).catch(n=>{e.innerHTML="Oops! There was a problem submitting your form"})}r&&r.addEventListener("submit",m);})();
